from localstack.runtime import hooks
from localstack.services.internal import get_internal_apis
from localstack_ext import config as config_ext
@hooks.on_infra_start(should_load=config_ext.ACTIVATE_PRO)
def register_restricted_pods_api():from localstack_ext.persistence.pods_api import CloudPodsRestrictedApi as A;get_internal_apis().add(A())
@hooks.on_infra_start(should_load=config_ext.ACTIVATE_PRO)
def register_remotes_pods_api():from localstack_ext.persistence.remotes.api import CloudPodsRemotesApi as A;get_internal_apis().add(A())
@hooks.on_infra_start(should_load=config_ext.ACTIVATE_PRO)
def register_ci_run_manager():from localstack_ext.persistence.pods_api import CloudPodsCiRunApi as A;from localstack_ext.utils.cloud_pods import get_ci_run_manager as B;get_internal_apis().add(A());B().startup()
@hooks.on_infra_shutdown(should_load=config_ext.ACTIVATE_PRO)
def shutdown_ci_run_manager():from localstack_ext.utils.cloud_pods import get_ci_run_manager as A;A().shutdown()